create table language (id NUMBER(7) auto_increment NOT NULL PRIMARY KEY, description VARCHAR(50) NOT NULL);
CREATE TABLE author (id NUMBER(7) auto_increment NOT NULL PRIMARY KEY, first_name VARCHAR(50), last_name VARCHAR(50) NOT NULL, date_of_birth DATE);
create table book (id NUMBER(7) auto_increment NOT NULL PRIMARY KEY, author_id NUMBER(7) NOT NULL, language_id NUMBER(7) NOT NULL, name VARCHAR(50) NOT NULL, published_in NUMBER(7) NOT NULL, CONSTRAINT fk_book_author FOREIGN KEY (author_id) REFERENCES author(id), CONSTRAINT fk_book_language   FOREIGN KEY (language_id) REFERENCES language(id));
